function App() {
    return (
        <Router>
        <div className="App">
           <header className="App-header">
             <h1>Welcome to Cupboard Software</h1>
             <p>Manage your business with ease!</p>
           </header>
           <main>
             <Switch>
               <Route exact path="/">
                 <Dashboard />
               </Route>
               <Route path="/invoices">
                 <Invoices />
               </Route>
               <Route path="/inventory">
                 <Inventory />
               </Route>
               <Route path="/expenses">
                 <Expenses />
               </Route>
               <Route path="/reports">
                 <Reports />
               </Route>
               <Route path="/gst-reports">
                 <GSTReports />
               </Route>
               <Route path="/payments">
                 <Payments />
               </Route>
               <Route path="/security">
                 <Security />
               </Route>
               <Route path="/signin">
                 <SignIn />
               </Route>
             </Switch>
          </main>
         </div>
       </Router>
    );
  }
  
export default App;
