// src/components/Inventory/Inventory.js
import React, { useEffect, useState } from 'react';
import { getFirestore } from 'firebase/firestore';
 // Make sure to import firestore correctly

const Inventory = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [inventoryItems, setInventoryItems] = useState([]);

  useEffect(() => {
    const fetchInventoryItems = async () => {
      try {
        const snapshot = await firestore.collection('inventory').get();
        const itemsList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setInventoryItems(itemsList);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching inventory items: ', error);
        setIsLoading(false);
        // Handle error state if needed
      }
    };

    fetchInventoryItems();
  }, []);

  return (
    <div>
      <h2>Inventory</h2>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {inventoryItems.map(item => (
            <li key={item.id}>
              <strong>Name:</strong> {item.name} | <strong>Quantity:</strong> {item.quantity}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Inventory;
